import { StyleSheet, Image, Text, View } from "react-native";
import React from "react";
import { createMaterialTopTabNavigator } from "@react-navigation/material-top-tabs";
import HopiScreen from "../screens/HopiScreen";
import HopiShopScreen from "../screens/HopiShopScreen";
import { useSafeAreaInsets } from "react-native-safe-area-context";

const Tab = createMaterialTopTabNavigator();

const HomeRouter = () => {
  const insets = useSafeAreaInsets();

  return (
    <Tab.Navigator
      screenOptions={{
        tabBarShowLabel: false,
        tabBarStyle: { paddingVertical: 5 },
        tabBarIndicatorStyle: {borderBottomColor: "#A94C79", borderBottomWidth: 2}
      }}
      style={{
        paddingTop: insets.top,
        paddingBottom: insets.bottom,
        paddingLeft: insets.left,
        paddingRight: insets.right,
      }}
    >
      <Tab.Screen
        name="Hopi"
        component={HopiScreen}
        options={{
          tabBarIcon: () => {
            return (
              <Image
                style={styles.image}
                source={require(// @ts-ignore
                "../../assets/hopi.png")}
              />
            );
          },
        }}
      />
      <Tab.Screen
        name="HopiShop"
        component={HopiShopScreen}
        options={{
          tabBarIcon: () => {
            return (
              <Image
                style={styles.image}
                source={require(// @ts-ignore
                "../../assets/hopishop.jpg")}
              />
            );
          },
        }}
      />
    </Tab.Navigator>
  );
};

export default HomeRouter;

const styles = StyleSheet.create({
  image: {
    width: 40,
    height: 40,
  },
});
