import {
  StyleSheet,
  View,
  Text,
  Image,
  FlatList,
  ImageBackground,
  ScrollView,
  Dimensions,
  TouchableOpacity,
} from "react-native";
import { hopiImages, sectionImages } from "./../data/hopiImages";
import React, { useCallback, useRef, useState } from "react";
import SectionItem from "../components/SectionItem";

const HopiScreen = () => {
  const [imageNumber, setImageNumber] = useState("1");

  return (
    <View style={{ flex: 1 }}>
      <ScrollView>
        {/* Top */}
        <View style={styles.top}>
          <View style={styles.account}>
            <Text>üôã‚Äç‚ôÄÔ∏è</Text>
            <Text style={styles.accountTitle}>Merhaba</Text>
            <Text style={styles.accountTitle}>Didem</Text>
          </View>
          <View style={styles.topRight}>
            <Text style={styles.topRightText1}>201.50 Paracik'in var</Text>
            <Text style={styles.topRightText2}>
              1 Paracik=1 TL degerinde kullanabilirsin
            </Text>
            <Text style={styles.topRightText3}>Paraciklarima Git</Text>
          </View>
        </View>

        <View style={styles.middle}>
          <View style={styles.middleTop}>
            <Text style={{ fontSize: 18, fontWeight: "700", color: "#474747" }}>
              Teklifi Incele
            </Text>
            <View>
              <Text
                style={{
                  color: "#474747",
                  fontSize: 10,
                  backgroundColor: "#E0E0E0",
                  paddingVertical: 5,
                  paddingHorizontal: 10,
                  borderRadius: 15,
                }}
              >
                {imageNumber}/15
              </Text>
            </View>
          </View>
          {/*Hopi Images */}
          <FlatList
            data={hopiImages}
            keyExtractor={(item) => item.id.toString()}
            horizontal={true}
            showsHorizontalScrollIndicator={false}
            ItemSeparatorComponent={() => <View style={styles.separator} />}
            onMomentumScrollEnd={(event) => {
              const index = Math.floor(event.nativeEvent.contentOffset.x);
              let rest = (index / 320 + 1).toFixed();

              setImageNumber(rest);
            }}
            renderItem={({ item }) => (
              <View>
                <Image source={item.image} style={styles.image} />
              </View>
            )}
          />

          {/*Section Images */}
          <TouchableOpacity >
            <Image source={sectionImages[0].image} style={{resizeMode:'contain', width:"100%"}} />
          </TouchableOpacity>
          <View>
            {sectionImages.map((item, index) => (
              <View key={index}>
                <Image source={item.image} style={styles.sectionImage} />
              </View>
            ))}
          </View>
          {/* Sevecegin Kategoriler */}
          <View>
              <Text style={{ fontSize: 18, fontWeight: "700", color: "#474747" }}>Sevecegin Kategoriler</Text>
          </View>
        </View>
      </ScrollView>
    </View>
  );
};

export default HopiScreen;

const styles = StyleSheet.create({
  top: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-around",
    marginTop: 10,
  },
  account: {
    backgroundColor: "white",
    justifyContent: "center",
    paddingVertical: 20,
    paddingHorizontal: 10,
    borderRadius: 15,
    width: "30%",
  },
  accountTitle: {
    fontSize: 12,
    fontWeight: "700",
    color: "#474747",
  },
  topRight: {
    backgroundColor: "white",
    justifyContent: "center",
    paddingVertical: 15,
    paddingHorizontal: 10,
    borderRadius: 15,
    width: "63%",
    color: "#474747",
  },
  topRightText1: {
    color: "#E7C85C",
    fontWeight: "800",
  },
  topRightText2: {
    fontSize: 10,
    marginVertical: 10,
  },
  topRightText3: {
    fontSize: 12,
    textDecorationLine: "underline",
  },
  middle: {
    marginVertical: 15,
    paddingHorizontal: 15,
  },
  middleTop: {
    display: "flex",
    flexDirection: "row",
    justifyContent: "space-between",
  },
  image: {
    width: 320,
    height: 200,
    borderRadius: 10,
    marginVertical: 10,
  },
  separator: {
    width: 12,
  },
  sectionImage: {
    width: 320,
    height: 150,
    marginBottom: 10,
    borderRadius: 10,
  },
});
